import{_ as l,c as o,o as t,a4 as i}from"./chunks/framework.CpVK_f0P.js";const d=JSON.parse('{"title":"音标分类","description":"","frontmatter":{"title":"音标分类"},"headers":[],"relativePath":"pronunciation/phonetic-alphabet-classification.md","filePath":"pronunciation/phonetic-alphabet-classification.md"}'),s={name:"pronunciation/phonetic-alphabet-classification.md"},n=i('<h1 id="音标分类" tabindex="-1">音标分类 <a class="header-anchor" href="#音标分类" aria-label="Permalink to &quot;音标分类&quot;">​</a></h1><p>音标可以根据发音方式、发音部位、音素类型、音长、音高、舌位、音节以及音素组合等标准进行分类。以下是详细的分类及其作用。</p><h2 id="按发音方式分类" tabindex="-1">按发音方式分类 <a class="header-anchor" href="#按发音方式分类" aria-label="Permalink to &quot;按发音方式分类&quot;">​</a></h2><ol><li><p><strong>爆破音（Plosives）</strong></p><ul><li><strong>定义</strong>：发音时气流在口腔或喉咙中被完全阻塞，然后突然释放。</li><li><strong>音标</strong>：/p/, /b/, /t/, /d/, /k/, /ɡ/</li><li><strong>例子</strong>： <ul><li>/p/ - &quot;pat&quot;</li><li>/b/ - &quot;bat&quot;</li><li>/t/ - &quot;top&quot;</li><li>/d/ - &quot;dog&quot;</li><li>/k/ - &quot;cat&quot;</li><li>/ɡ/ - &quot;go&quot;</li></ul></li></ul></li><li><p><strong>摩擦音（Fricatives）</strong></p><ul><li><strong>定义</strong>：发音时气流通过一个狭窄的通道，产生摩擦声。</li><li><strong>音标</strong>：/f/, /v/, /θ/, /ð/, /s/, /z/, /ʃ/, /ʒ/, /h/</li><li><strong>例子</strong>： <ul><li>/f/ - &quot;fish&quot;</li><li>/v/ - &quot;van&quot;</li><li>/θ/ - &quot;think&quot;</li><li>/ð/ - &quot;this&quot;</li><li>/s/ - &quot;see&quot;</li><li>/z/ - &quot;zoo&quot;</li><li>/ʃ/ - &quot;she&quot;</li><li>/ʒ/ - &quot;measure&quot;</li><li>/h/ - &quot;hat&quot;</li></ul></li></ul></li><li><p><strong>鼻音（Nasals）</strong></p><ul><li><strong>定义</strong>：发音时气流通过鼻腔。</li><li><strong>音标</strong>：/m/, /n/, /ŋ/</li><li><strong>例子</strong>： <ul><li>/m/ - &quot;man&quot;</li><li>/n/ - &quot;no&quot;</li><li>/ŋ/ - &quot;sing&quot;</li></ul></li></ul></li><li><p><strong>闪音（Flap）</strong></p><ul><li><strong>定义</strong>：发音时舌尖快速接触并离开上齿龈。</li><li><strong>音标</strong>：/ɾ/</li><li><strong>例子</strong>： <ul><li>/ɾ/ - &quot;better&quot;（美式英语中）</li></ul></li></ul></li><li><p><strong>近音（Approximants）</strong></p><ul><li><strong>定义</strong>：发音时发音器官接近但不完全接触，气流通过时不产生摩擦。</li><li><strong>音标</strong>：/r/, /j/, /w/, /l/</li><li><strong>例子</strong>： <ul><li>/r/ - &quot;red&quot;</li><li>/j/ - &quot;yes&quot;</li><li>/w/ - &quot;we&quot;</li><li>/l/ - &quot;light&quot;</li></ul></li></ul></li><li><p><strong>塞擦音（Affricates）</strong></p><ul><li><strong>定义</strong>：发音时先出现爆破音，然后紧接着摩擦音。</li><li><strong>音标</strong>：/tʃ/, /dʒ/</li><li><strong>例子</strong>： <ul><li>/tʃ/ - &quot;chop&quot;</li><li>/dʒ/ - &quot;jump&quot;</li></ul></li></ul></li><li><p><strong>半元音（Semi-vowels）</strong></p><ul><li><strong>定义</strong>：发音时气流几乎不受阻碍，类似元音，但在音节中起辅音的作用。</li><li><strong>音标</strong>：/j/, /w/</li><li><strong>例子</strong>： <ul><li>/j/ - &quot;yes&quot;</li><li>/w/ - &quot;we&quot;</li></ul></li></ul></li></ol><h2 id="按发音部位分类" tabindex="-1">按发音部位分类 <a class="header-anchor" href="#按发音部位分类" aria-label="Permalink to &quot;按发音部位分类&quot;">​</a></h2><ol><li><p><strong>双唇音（Bilabials）</strong></p><ul><li><strong>定义</strong>：发音时上下唇接触。</li><li><strong>音标</strong>：/p/, /b/, /m/, /w/</li><li><strong>例子</strong>： <ul><li>/p/ - &quot;pat&quot;</li><li>/b/ - &quot;bat&quot;</li><li>/m/ - &quot;man&quot;</li><li>/w/ - &quot;we&quot;</li></ul></li></ul></li><li><p><strong>唇齿音（Labiodentals）</strong></p><ul><li><strong>定义</strong>：发音时下唇接触上齿。</li><li><strong>音标</strong>：/f/, /v/</li><li><strong>例子</strong>： <ul><li>/f/ - &quot;fish&quot;</li><li>/v/ - &quot;van&quot;</li></ul></li></ul></li><li><p><strong>齿音（Dentals）</strong></p><ul><li><strong>定义</strong>：发音时舌尖接触上齿。</li><li><strong>音标</strong>：/θ/, /ð/</li><li><strong>例子</strong>： <ul><li>/θ/ - &quot;think&quot;</li><li>/ð/ - &quot;this&quot;</li></ul></li></ul></li><li><p><strong>齿龈音（Alveolars）</strong></p><ul><li><strong>定义</strong>：发音时舌尖接触齿龈。</li><li><strong>音标</strong>：/t/, /d/, /s/, /z/, /n/, /l/, /ɾ/</li><li><strong>例子</strong>： <ul><li>/t/ - &quot;top&quot;</li><li>/d/ - &quot;dog&quot;</li><li>/s/ - &quot;see&quot;</li><li>/z/ - &quot;zoo&quot;</li><li>/n/ - &quot;no&quot;</li><li>/l/ - &quot;light&quot;</li><li>/ɾ/ - &quot;better&quot;（美式英语中）</li></ul></li></ul></li><li><p><strong>硬腭音（Palatals）</strong></p><ul><li><strong>定义</strong>：发音时舌前部接触硬腭。</li><li><strong>音标</strong>：/ʃ/, /ʒ/, /tʃ/, /dʒ/, /j/</li><li><strong>例子</strong>： <ul><li>/ʃ/ - &quot;she&quot;</li><li>/ʒ/ - &quot;measure&quot;</li><li>/tʃ/ - &quot;chop&quot;</li><li>/dʒ/ - &quot;jump&quot;</li><li>/j/ - &quot;yes&quot;</li></ul></li></ul></li><li><p><strong>软腭音（Velars）</strong></p><ul><li><strong>定义</strong>：发音时舌后部接触软腭。</li><li><strong>音标</strong>：/k/, /ɡ/, /ŋ/</li><li><strong>例子</strong>： <ul><li>/k/ - &quot;cat&quot;</li><li>/ɡ/ - &quot;go&quot;</li><li>/ŋ/ - &quot;sing&quot;</li></ul></li></ul></li><li><p><strong>喉音（Glottals）</strong></p><ul><li><strong>定义</strong>：发音时声门阻塞或收缩。</li><li><strong>音标</strong>：/h/, /ʔ/</li><li><strong>例子</strong>： <ul><li>/h/ - &quot;hat&quot;</li><li>/ʔ/ - &quot;uh-oh&quot;（喉塞音）</li></ul></li></ul></li></ol><h2 id="按音素类型分类" tabindex="-1">按音素类型分类 <a class="header-anchor" href="#按音素类型分类" aria-label="Permalink to &quot;按音素类型分类&quot;">​</a></h2><ol><li><p><strong>元音（Vowels）</strong></p><ul><li><strong>定义</strong>：发音时气流不受阻碍地通过口腔。</li><li><strong>分类</strong>： <ul><li><strong>单元音（Monophthongs）</strong>：发音时口腔位置不变。 <ul><li><strong>前元音</strong>：/iː/, /ɪ/, /e/, /æ/</li><li><strong>中元音</strong>：/ə/, /ɜː/, /ʌ/</li><li><strong>后元音</strong>：/uː/, /ʊ/, /ɔː/, /ɒ/, /ɑː/</li></ul></li><li><strong>双元音（Diphthongs）</strong>：发音时口腔位置发生变化。 <ul><li>/eɪ/, /aɪ/, /ɔɪ/, /aʊ/, /əʊ/, /ɪə/, /eə/, /ʊə/</li></ul></li></ul></li></ul></li><li><p><strong>辅音（Consonants）</strong></p><ul><li><strong>定义</strong>：发音时气流在口腔或喉咙中受到阻碍。</li><li><strong>分类</strong>： <ul><li><strong>清辅音（Voiceless Consonants）</strong>：发音时声带不振动。 <ul><li>/p/, /t/, /k/, /f/, /θ/, /s/, /ʃ/, /h/</li></ul></li><li><strong>浊辅音（Voiced Consonants）</strong>：发音时声带振动。 <ul><li>/b/, /d/, /ɡ/, /v/, /ð/, /z/, /ʒ/, /m/, /n/, /ŋ/, /l/, /r/, /j/, /w/</li></ul></li></ul></li></ul></li></ol><h2 id="按音长分类" tabindex="-1">按音长分类 <a class="header-anchor" href="#按音长分类" aria-label="Permalink to &quot;按音长分类&quot;">​</a></h2><ol><li><p><strong>长元音（Long Vowels）</strong></p><ul><li><strong>定义</strong>：发音时间较长的元音。</li><li><strong>音标</strong>：/iː/, /ɑː/, /ɔː/, /uː/, /ɜː/</li><li><strong>例子</strong>： <ul><li>/iː/ - &quot;see&quot;</li><li>/ɑː/ - &quot;father&quot;</li><li>/ɔː/ - &quot;law&quot;</li><li>/uː/ - &quot;blue&quot;</li><li>/ɜː/ - &quot;bird&quot;</li></ul></li></ul></li><li><p><strong>短元音（Short Vowels）</strong></p><ul><li><strong>定义</strong>：发音时间较短的元音。</li><li><strong>音标</strong>：/ɪ/, /e/, /æ/, /ʌ/, /ɒ/, /ʊ/, /ə/</li><li><strong>例子</strong>： <ul><li>/ɪ/ - &quot;sit&quot;</li><li>/e/ - &quot;bed&quot;</li><li>/æ/ - &quot;cat&quot;</li><li>/ʌ/ - &quot;cup&quot;</li><li>/ɒ/ - &quot;pot&quot;</li><li>/ʊ/ - &quot;good&quot;</li><li>/ə/ - &quot;sofa&quot;</li></ul></li></ul></li></ol><h2 id="按音高分类" tabindex="-1">按音高分类 <a class="header-anchor" href="#按音高分类" aria-label="Permalink to &quot;按音高分类&quot;">​</a></h2><ol><li><p><strong>高音（High Pitch）</strong></p><ul><li><strong>定义</strong>：发音时声带紧张且振动频率较高，通常音高较高。</li><li><strong>音标</strong>：无特定音标，但常见于语调变化中。</li><li><strong>例子</strong>：在英语中用于表示疑问句时，如&quot;Are you coming?&quot;</li></ul></li><li><p><strong>中音（Mid Pitch）</strong></p><ul><li><strong>定义</strong>：发音时声带适度紧张，振动频率适中。</li><li><strong>音标</strong>：无特定音标，常见于正常陈述句中。</li><li><strong>例子</strong>：&quot;She is reading a book.&quot;</li></ul></li><li><p><strong>低音（Low Pitch）</strong></p><ul><li><strong>定义</strong>：发音时声带放松且振动频率较低，通常音高较低。</li><li><strong>音标</strong>：无特定音标，但常见于语调变化中。</li><li><strong>例子</strong>：在英语中用于表示陈述或命令时，如&quot;Close the door.&quot;</li></ul></li></ol><h2 id="按舌位分类" tabindex="-1">按舌位分类 <a class="header-anchor" href="#按舌位分类" aria-label="Permalink to &quot;按舌位分类&quot;">​</a></h2><ol><li><p><strong>前元音（Front Vowels）</strong></p><ul><li><strong>定义</strong>：发音时舌头位于口腔前部。</li><li><strong>音标</strong>：/iː/, /ɪ/, /e/, /æ/</li><li><strong>例子</strong>： <ul><li>/iː/ - &quot;see&quot;</li><li>/ɪ/ - &quot;sit&quot;</li><li>/e/ - &quot;bed&quot;</li><li>/æ/ - &quot;cat&quot;</li></ul></li></ul></li><li><p><strong>中元音（Central Vowels）</strong></p><ul><li><strong>定义</strong>：发音时舌头位于口腔中部。</li><li><strong>音标</strong>：/ə/, /ɜː/, /ʌ/</li><li><strong>例子</strong>： <ul><li>/ə/ - &quot;sofa&quot;</li><li>/ɜː/ - &quot;bird&quot;</li><li>/ʌ/ - &quot;cup&quot;</li></ul></li></ul></li><li><p><strong>后元音（Back Vowels）</strong></p><ul><li><strong>定义</strong>：发音时舌头位于口腔后部。</li><li><strong>音标</strong>：/uː/, /ʊ/, /ɔː/, /ɒ/, /ɑː/</li><li><strong>例子</strong>： <ul><li>/uː/ - &quot;blue&quot;</li><li>/ʊ/ - &quot;good&quot;</li><li>/ɔː/ - &quot;law&quot;</li><li>/ɒ/ - &quot;pot&quot;</li><li>/ɑː/ - &quot;father&quot;</li></ul></li></ul></li></ol><h2 id="按音节分类" tabindex="-1">按音节分类 <a class="header-anchor" href="#按音节分类" aria-label="Permalink to &quot;按音节分类&quot;">​</a></h2><ol><li><p><strong>单音节词（Monosyllabic Words）</strong></p><ul><li><strong>定义</strong>：只有一个音节的词。</li><li><strong>例子</strong>：cat, dog, book</li></ul></li><li><p><strong>双音节词（Disyllabic Words）</strong></p><ul><li><strong>定义</strong>：有两个音节的词。</li><li><strong>例子</strong>：apple, table, teacher</li></ul></li><li><p><strong>多音节词（Polysyllabic Words）</strong></p><ul><li><strong>定义</strong>：有两个以上音节的词。</li><li><strong>例子</strong>：elephant, university, communication</li></ul></li></ol><h2 id="按音素组合分类" tabindex="-1">按音素组合分类 <a class="header-anchor" href="#按音素组合分类" aria-label="Permalink to &quot;按音素组合分类&quot;">​</a></h2><ol><li><p><strong>辅音簇（Consonant Clusters）</strong></p><ul><li><strong>定义</strong>：两个或多个辅音音素连在一起发音。</li><li><strong>例子</strong>： <ul><li>/str/ - &quot;street&quot;</li><li>/spl/ - &quot;splash&quot;</li><li>/bl/ - &quot;black&quot;</li></ul></li></ul></li><li><p><strong>双元音（Diphthongs）</strong></p><ul><li><strong>定义</strong>：发音时口腔位置发生变化的两个元音音素组合。</li><li><strong>音标</strong>：/eɪ/, /aɪ/, /ɔɪ/, /aʊ/, /əʊ/, /ɪə/, /eə/, /ʊə/</li><li><strong>例子</strong>： <ul><li>/eɪ/ - &quot;say&quot;</li><li>/aɪ/ - &quot;my&quot;</li><li>/ɔɪ/ - &quot;boy&quot;</li><li>/aʊ/ - &quot;now&quot;</li><li>/əʊ/ - &quot;go&quot;</li><li>/ɪə/ - &quot;here&quot;</li><li>/eə/ - &quot;there&quot;</li><li>/ʊə/ - &quot;tour&quot;</li></ul></li></ul></li><li><p><strong>三元音（Triphthongs）</strong></p><ul><li><strong>定义</strong>：发音时口腔位置连续变化的三个元音音素组合。</li><li><strong>音标</strong>：/aɪə/, /aʊə/, /eɪə/, /ɔɪə/</li><li><strong>例子</strong>： <ul><li>/aɪə/ - &quot;fire&quot;</li><li>/aʊə/ - &quot;hour&quot;</li><li>/eɪə/ - &quot;player&quot;</li><li>/ɔɪə/ - &quot;employer&quot;</li></ul></li></ul></li></ol>',18),u=[n];function r(g,q,a,e,p,h){return t(),o("div",null,u)}const b=l(s,[["render",r]]);export{d as __pageData,b as default};
